   */
  confirmEmail(data) {
    if(typeof data !== "object")
    {
      data = {code: data}
    }
    return this.client.post({path: '/confirm-email', data: data})
      .then(response => {
        Event.send("wk-event-email-confirm", response);
        return response;
      })
