        return response;
      })
  }

  /**
   *
   * @returns {Promise<any>}
   */
  getCards() {
    return this.client.get({path: '/user/cards'})
      .then(response => {
        Event.send("wk-event-cards", response);
        return response;
      })
  }

  /**
